INSERT INTO `assignment` VALUES (70028, '第一章作业：函数与基本性质', 40004, 37, 'homework', '本次作业围绕函数的基本概念、常见初等函数的性质及其图像特征展开，要求学生能够分析函数的单调性、奇偶性和周期性，并通过简单实例理解函数在模型中的作用。', '2025-12-01 00:00:00', '2025-12-31 23:59:59', '2025-12-02 10:29:42', 1, NULL, 'file', NULL, 100, NULL, NULL, '[{\"url\": \"/courses/40004/37/chapter1_assignment.docx\", \"name\": \"chapter1_assignment.docx\"}]', 0, NULL);
INSERT INTO `assignment` VALUES (70029, '第二章作业：极限与连续', 40004, 37, 'homework', '本作业主要考查函数极限的求解方法、无穷小及无穷大的比较、函数连续性的判定，旨在提升学生对极限本质及逼近思想的理解能力。', '2025-12-01 00:00:00', '2025-12-31 23:59:59', '2025-12-02 10:29:42', 1, NULL, 'file', NULL, 100, NULL, NULL, '[{\"url\": \"/courses/40004/37/chapter2_assignment.docx\", \"name\": \"chapter2_assignment.docx\"}]', 0, NULL);
INSERT INTO `assignment` VALUES (70030, '第三章作业：导数与微分', 40004, 37, 'homework', '围绕导数的定义、求导法则和变化率含义展开，要求学生熟练掌握链式法则、隐函数求导和微分近似，在实际问题中合理应用导数工具。', '2025-12-01 00:00:00', '2025-12-31 23:59:59', '2025-12-02 10:29:42', 1, NULL, 'file', NULL, 100, NULL, NULL, '[{\"url\": \"/courses/40004/37/chapter3_assignment.docx\", \"name\": \"chapter3_assignment.docx\"}]', 0, NULL);
INSERT INTO `assignment` VALUES (70031, '第四章作业：中值定理与导数应用', 40004, 37, 'homework', '本作业聚焦罗尔定理、拉格朗日中值定理以及导数在函数单调区间、极值判断和曲线分析中的应用，帮助学生建立从局部性质分析整体行为的能力。', '2025-12-01 00:00:00', '2025-12-31 23:59:59', '2025-12-02 10:29:43', 1, NULL, 'file', NULL, 100, NULL, NULL, '[{\"url\": \"/courses/40004/37/chapter4_assignment.docx\", \"name\": \"chapter4_assignment.docx\"}]', 0, NULL);
INSERT INTO `assignment` VALUES (70032, '第五章作业：不定积分', 40004, 37, 'homework', '内容涵盖基本积分公式、换元法与分部积分法，要求学生能够根据 integrand 结构选择合适的积分方法，理解不定积分作为反导函数的核心意义。', '2025-12-01 00:00:00', '2025-12-31 23:59:59', '2025-12-02 10:29:43', 1, NULL, 'file', NULL, 100, NULL, NULL, '[{\"url\": \"/courses/40004/37/chapter5_assignment.docx\", \"name\": \"chapter5_assignment.docx\"}]', 0, NULL);
INSERT INTO `assignment` VALUES (70033, '第六章作业：定积分及应用', 40004, 37, 'homework', '本作业围绕定积分的定义、基本性质与牛顿-莱布尼茨公式展开，要求学生能够计算典型定积分，并将其应用于面积、体积及平均值等建模场景。', '2025-12-01 00:00:00', '2025-12-31 23:59:59', '2025-12-02 10:29:43', 1, NULL, 'file', NULL, 100, NULL, NULL, '[{\"url\": \"/courses/40004/37/chapter6_assignment.docx\", \"name\": \"chapter6_assignment.docx\"}]', 0, NULL);
INSERT INTO `assignment` VALUES (70034, '综合作业：高等数学第1-6章综合巩固', 40004, 37, 'homework', '综合覆盖函数性质、极限连续、导数微分、中值定理、不定积分及定积分等核心内容，通过多步骤综合题检验学生的整体掌握程度，强化知识体系的协调性。', '2025-12-01 00:00:00', '2025-12-31 23:59:59', '2025-12-02 10:29:43', 1, NULL, 'file', NULL, 100, NULL, NULL, '[{\"url\": \"/courses/40004/37/comprehensive_assignment.docx\", \"name\": \"comprehensive_assignment.docx\"}]', 0, NULL);
