INSERT INTO `course` VALUES (40001, '人工智能导论', '《人工智能导论》是面向高校本科生、零基础入门者开设的核心通识课程，旨在搭建人工智能领域的基础认知体系。课程以 “原理 + 场景” 为核心，从人工智能的起源、发展脉络与核心定义切入，系统讲解机器学习、深度学习、自然语言处理、计算机视觉等核心技术的底层逻辑，同时结合智能推荐、自动驾驶、智能客服等典型落地场景，拆解 AI 技术的应用边界与实现路径。通过本课程学习，学习者可掌握 AI 核心概念与基础原理，理解技术与产业的结合方式，具备识别 AI 应用场景、分析基础算法逻辑的能力，为后续深入学习 AI 细分方向或跨领域应用 AI 知识筑牢基础。', '/profile/upload/2025/12/01/屏幕截图 2025-12-01 024033_20251201024118A001.png', 3.0, '必修课', '2025-11-30 00:00:00', '2025-12-31 00:00:00', 37, '进行中', '2025春', 0, NULL, '2025-11-19 19:59:04', '2025-12-01 02:41:29', 0, NULL);
