INSERT INTO `course` VALUES (40004, '高等数学', '《高等数学》是面向理工科本科学生的核心基础课程，系统讲授微积分、空间解析几何与常微分方程的基本理论与方法。内容涵盖函数与极限、导数与微分、积分学、多元函数微积分、级数及微分方程等。注重逻辑思维训练与抽象建模能力培养，为后续专业课程学习和科学研究奠定坚实的数学基础。', '/profile/upload/2025/11/30/屏幕截图 2025-11-28 224929_20251130092009A001.png', 3.0, '必修课', '2025-11-05 00:00:00', '2025-12-06 00:00:00', 37, '进行中', '2025春', 0, NULL, '2025-11-30 08:59:07', '2025-12-02 15:30:44', 0, NULL);
INSERT INTO `course` VALUES (40005, '计算机组成原理', '《计算机组成原理》是计算机科学与技术专业的核心基础课程，系统介绍计算机硬件系统的组成结构与工作原理。课程涵盖数据表示、算术逻辑单元、处理器架构、指令系统、存储系统及输入输出机制等内容，重点讲解冯·诺依曼体系结构和现代计算机的运行机制。通过本课程，学生将掌握计算机各功能部件的工作原理及其相互协作方式，为后续学习操作系统、编译原理和计算机体系结构等课程奠定坚实基础。', '/profile/upload/2025/12/01/2_20251201140003A001.png', 3.0, '必修课', '2025-11-19 00:00:00', '2025-12-05 00:00:00', 37, '进行中', '2025春', 0, NULL, '2025-12-01 14:00:14', '2025-12-02 15:13:42', 0, NULL);
