INSERT INTO `course_competency` VALUES (1, 40004, '极限计算与应用能力', '能理解极限的定义、性质，熟练运用四则运算法则、等价无穷小、洛必达法则等方法求解各类极限，解决实际问题', 1, 0.20, '2025-11-26 10:11:53', '2025-11-26 17:01:06', 0);
INSERT INTO `course_competency` VALUES (2, 40004, '函数分析与建模能力', '能掌握函数的定义、性质（奇偶性、单调性、周期性）、反函数与复合函数，具备简单函数建模能力', 2, 0.25, '2025-11-26 10:11:53', '2025-11-26 17:00:52', 0);
INSERT INTO `course_competency` VALUES (3, 40004, '导数概念与运算能力', '能理解导数的定义、几何意义，熟练运用基本导数公式、复合函数求导法则计算导数', 3, 0.20, '2025-11-26 10:11:53', '2025-11-26 17:01:03', 0);
INSERT INTO `course_competency` VALUES (4, 40004, '函数连续性分析能力', '能判断函数的连续性，掌握连续的充要条件，识别间断点类型', 4, 0.15, '2025-11-26 10:11:53', '2025-11-26 17:01:13', 0);
