INSERT INTO `knowledge_point` VALUES (60004, 40002, '缩进规则', '指 Python 中通过空格或制表符来表示代码块层级关系的语法规则，是 Python 区别于其他语言的显著特征。错误的缩进（如缩进不一致、缩进数量错误）会直接导致 IndentationError，影响代码执行。', 'BASIC', 20004, '2025-11-21 16:46:15', '2025-11-21 16:46:15', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60005, 40002, '变量命名规范', 'Python 中变量命名需遵循的规则，包括只能以字母或下划线开头、区分大小写、不能使用关键字（如 if、for 等）作为变量名等。不符合规范的命名可能导致语法错误，或降低代码可读性。', 'BASIC', 20004, '2025-11-21 16:46:15', '2025-11-21 16:46:15', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60006, 40002, '数据类型及转换', 'Python 中基本数据类型（int、float、str、bool 等）的特性及相互转换方法（如 int ()、str ()、float () 函数）。数据类型转换错误（如将非数字字符串转为 int）会引发 TypeError 或 ValueError。', 'BASIC', 20004, '2025-11-21 16:46:15', '2025-11-28 02:33:15', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60007, 40002, '条件语句（if-elif-else）', '用于根据条件执行不同代码块的控制结构，包括单分支、多分支判断逻辑。常见错误包括冒号遗漏、条件表达式逻辑错误等，会导致代码执行不符合预期。', 'BASIC', 20004, '2025-11-21 16:46:15', '2025-11-28 02:33:15', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60008, 40002, '循环结构（for/while）', '用于重复执行代码块的控制结构，for 循环适用于遍历序列（如列表、字符串），while 循环适用于满足条件时持续执行。常见错误包括死循环、循环变量未更新等。', 'BASIC', 20004, '2025-11-21 16:46:15', '2025-11-21 16:46:15', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60009, 40002, '函数定义与调用', '封装可复用代码的语法结构，包括 def 关键字定义函数、参数传递、返回值（return）等。常见错误包括参数数量不匹配、未定义返回值却尝试使用返回结果等。', 'INTERMEDIATE', 20004, '2025-11-21 16:46:15', '2025-11-21 16:46:15', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60010, 40002, '类与对象', '面向对象编程的核心概念，类是对象的模板（定义属性和方法），对象是类的实例。通过 class 关键字定义类，通过实例化创建对象，是理解继承、多态的基础。', 'INTERMEDIATE', 20004, '2025-11-21 16:46:15', '2025-11-21 16:46:15', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60011, 40002, '继承与多态', '面向对象编程的重要特性，继承允许子类复用父类的属性和方法并扩展功能；多态指不同子类对同一方法可有不同实现，提高代码灵活性。', 'ADVANCED', 20004, '2025-11-21 16:46:15', '2025-11-21 16:46:15', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60012, 40002, 'pandas 数据结构（Series 与 DataFrame）', '数据分析库 pandas 的核心数据结构，Series 是一维带标签数组，DataFrame 是二维表格型数据结构（由多个 Series 组成），是数据清洗、分析的基础。', 'INTERMEDIATE', 20004, '2025-11-21 16:46:16', '2025-11-21 16:46:16', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60013, 40002, '数据清洗方法', '数据分析中的预处理步骤，包括处理缺失值（dropna ()、fillna ()）、去除重复值（drop_duplicates ()）、数据格式转换等，目的是保证数据质量，为后续分析提供可靠基础。', 'INTERMEDIATE', 20004, '2025-11-21 16:46:16', '2025-11-21 16:46:16', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60014, 40002, 'matplotlib 数据可视化', 'Python 绘图库 matplotlib 的使用方法，包括绘制折线图、柱状图、直方图等，通过设置坐标轴、标题、图例等美化图表，将数据以直观方式呈现。', 'INTERMEDIATE', 20004, '2025-11-21 16:46:16', '2025-11-21 16:46:16', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60015, 40002, '异常处理（try-except）', '用于捕获和处理代码运行时错误的机制，通过 try 块执行可能出错的代码，except 块捕获指定异常并处理，避免程序直接崩溃，常见异常包括 ValueError、TypeError 等。', 'INTERMEDIATE', 20004, '2025-11-21 16:46:16', '2025-11-21 16:46:16', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60016, 40002, '模块与包的使用', 'Python 中组织代码的方式，模块是单个.py 文件，包是包含多个模块的目录（需含__init__.py）。通过 import 语句导入模块 / 包，实现代码复用，如 import pandas、from matplotlib import pyplot。', 'INTERMEDIATE', 20004, '2025-11-21 16:46:16', '2025-11-21 16:46:16', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60017, 40002, '列表与字典操作', 'Python 中常用的内置数据结构操作，列表（list）支持索引、切片、增删元素（append ()、pop ()）；字典（dict）通过键值对存储数据，支持增删改查（get ()、pop ()），是处理集合数据的基础。', 'BASIC', 20004, '2025-11-21 16:46:16', '2025-11-21 16:46:16', 0, NULL);
