INSERT INTO `knowledge_point` VALUES (60423, 40004, '抓大头法', '在求多项式极限时，只需关注分子和分母的最高次项，忽略低次项', 'BASIC', 19, '2025-12-02 21:42:42', '2025-12-02 21:42:42', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60424, 40004, '去心邻域', '一个点的去心邻域是指不包含该点本身的邻域', 'BASIC', 19, '2025-12-02 21:42:42', '2025-12-02 21:42:42', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60425, 40004, '极限的条件', '极限的运算需要内外层函数都满足特定条件，例如内层函数趋近于某点但不等于该点', 'BASIC', 19, '2025-12-02 21:42:43', '2025-12-02 21:42:42', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60426, 40004, '复合函数的极限运算', '复合函数的极限运算需要保证内层函数的极限值在定义域内，且外层函数在该极限值处连续', 'BASIC', 19, '2025-12-02 21:42:43', '2025-12-02 21:42:43', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60427, 40004, '单调性', '函数在某个区间上保持递增或递减的性质', 'BASIC', 19, '2025-12-02 21:48:51', '2025-12-02 21:48:50', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60428, 40004, '导数的符号与单调性', '导数大于零则函数在该区间单调递增，导数小于零则单调递减', 'BASIC', 19, '2025-12-02 21:48:51', '2025-12-02 21:48:51', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60429, 40004, '函数的连续性', '函数在某点附近取值的极限等于函数值', 'BASIC', 19, '2025-12-02 21:48:51', '2025-12-02 21:48:51', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60430, 40004, '函数的凹凸性', '函数图像在某区间上是否向上或向下弯曲的性质', 'BASIC', 19, '2025-12-02 21:48:51', '2025-12-02 21:48:51', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60431, 40004, '曲线的凹凸性定义', '若曲线上任意两点之间的弦在该点的函数值之上（或之下），则称为凹（或凸）函数', 'BASIC', 19, '2025-12-02 21:48:51', '2025-12-02 21:48:51', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60432, 40004, '切线', '在函数图像上某一点处的极限直线，表示该点的瞬时变化率', 'BASIC', 19, '2025-12-02 21:48:51', '2025-12-02 21:48:51', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60433, 40004, '导数的判定单调性方法', '通过求导并分析导数在区间内的符号来判断函数的单调性', 'BASIC', 19, '2025-12-02 21:48:52', '2025-12-02 21:48:51', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60434, 40004, '极值点', '函数在某点附近取得极大值或极小值的点', 'BASIC', 19, '2025-12-02 21:48:52', '2025-12-02 21:48:52', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60435, 40004, '函数的极值判定', '函数在某点取得极值，当该点是导数为零或不存在，并且该点的导数符号发生变化', 'BASIC', 19, '2025-12-02 21:48:52', '2025-12-02 21:48:52', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60436, 40004, '函数连续性', '函数在某一点的极限值等于该点的函数值', 'BASIC', 19, '2025-12-02 21:48:52', '2025-12-02 21:48:52', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60437, 40004, '二阶导数', '函数导数的导数，描述函数的变化率的变化率', 'BASIC', 19, '2025-12-02 21:48:52', '2025-12-02 21:48:52', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60438, 40004, '凹凸性', '函数图像在某区间内向上或向下弯曲的性质', 'BASIC', 19, '2025-12-02 21:48:52', '2025-12-02 21:48:52', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60439, 40004, '拐点', '函数图像由凸变凹或由凹变凸的点', 'BASIC', 19, '2025-12-02 21:48:53', '2025-12-02 21:48:52', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60440, 40004, '导数判定凹凸性', '利用一阶或二阶导数的符号来判断函数的凹凸性', 'BASIC', 19, '2025-12-02 21:48:53', '2025-12-02 21:48:53', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60441, 40004, '导数为零的点', '函数的一阶导数在该点为零的点，可能是极值点或拐点', 'BASIC', 19, '2025-12-02 21:48:53', '2025-12-02 21:48:53', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60442, 40004, '多项式函数', '由多个项相加而成的函数，每一项都是变量的整数次幂乘以系数', 'BASIC', 19, '2025-12-02 21:48:53', '2025-12-02 21:48:53', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60443, 40004, '导数不存在的点', '在该点处函数不可导，例如在尖点、断点或垂直切线处', 'BASIC', 19, '2025-12-02 21:48:54', '2025-12-02 21:48:54', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60444, 40004, '单调性判定', '通过一阶导数的符号来判断函数在某个区间内是单调递增还是单调递减', 'BASIC', 19, '2025-12-02 21:48:54', '2025-12-02 21:48:54', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60445, 40004, '导数的符号', '若一阶导数大于零，则函数在该点附近单调递增；小于零则单调递减', 'BASIC', 19, '2025-12-02 21:48:54', '2025-12-02 21:48:54', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60446, 40004, '初等函数', '由基本初等函数通过有限次的四则运算和复合运算构成的函数', 'BASIC', 19, '2025-12-02 21:48:55', '2025-12-02 21:48:54', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60447, 40004, '高阶导数', '函数在某点的导数的导数，用于描述函数变化的更高阶率', 'BASIC', 19, '2025-12-02 21:54:24', '2025-12-02 21:54:24', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60448, 40004, '微分', '函数在某点的线性近似，表示函数值的改变量与自变量改变量之间的关系', 'BASIC', 19, '2025-12-02 21:54:24', '2025-12-02 21:54:24', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60449, 40004, '可微', '函数在某点存在微分，即可以用一个多项式近似代替该函数', 'BASIC', 19, '2025-12-02 21:54:24', '2025-12-02 21:54:24', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60450, 40004, '泰勒公式', '用多项式近似表示函数的一种方法，其中多项式的系数由函数在某点的各阶导数值确定', 'BASIC', 19, '2025-12-02 21:54:24', '2025-12-02 21:54:24', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60451, 40004, '泰勒多项式', '泰勒公式的多项式部分，用于近似表示函数在某点附近的行为', 'BASIC', 19, '2025-12-02 21:54:25', '2025-12-02 21:54:24', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60452, 40004, '多项式近似', '用多项式函数来近似表示一个更复杂的函数，使计算或分析更简便', 'BASIC', 19, '2025-12-02 21:54:25', '2025-12-02 21:54:25', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60453, 40004, '余项', '泰勒多项式与原函数之间的差，用于衡量近似误差的大小', 'BASIC', 19, '2025-12-02 21:54:25', '2025-12-02 21:54:25', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60454, 40004, '麦克劳林公式', '泰勒公式在展开点为0时的特例，即用多项式表示函数在零点附近的展开', 'BASIC', 19, '2025-12-02 21:54:25', '2025-12-02 21:54:25', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60455, 40004, '拉格朗日余项', '泰勒展开的余项形式为Rn = (f^(n+1)(ξ)/(n+1)!) * (x - x0)^(n+1)，其中ξ在x和x0之间', 'BASIC', 19, '2025-12-02 21:54:25', '2025-12-02 21:54:25', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60456, 40004, '导数存在性', '函数在某点存在导数意味着该点的局部线性近似有意义，且变化率可计算', 'BASIC', 19, '2025-12-02 21:54:26', '2025-12-02 21:54:25', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60457, 40004, '函数在某点的性质', '函数在某点的连续性、可导性等特性决定了能否用泰勒多项式近似表示', 'BASIC', 19, '2025-12-02 21:54:26', '2025-12-02 21:54:26', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60458, 40004, '局部形态', '函数在某一点附近的表现形式或性质', 'BASIC', 19, '2025-12-02 21:54:26', '2025-12-02 21:54:26', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60459, 40004, '整体形态', '函数在整个定义域或区间上的表现形式或性质', 'BASIC', 19, '2025-12-02 21:54:26', '2025-12-02 21:54:26', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60460, 40004, '泰勒展开式', '函数在某点附近用其导数构成的级数表示', 'BASIC', 19, '2025-12-02 21:54:26', '2025-12-02 21:54:26', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60461, 40004, '等价无穷小', '两个无穷小量在极限过程中趋于同一速度', 'BASIC', 19, '2025-12-02 21:54:27', '2025-12-02 21:54:26', 0, NULL);
INSERT INTO `knowledge_point` VALUES (60462, 40004, '导数定义', '函数在某点的导数是该点的极限形式，即函数变化率的极限值', 'BASIC', 19, '2025-12-02 21:54:27', '2025-12-02 21:54:27', 0, NULL);
