mapper.select:


     and id = #{id}
     and assignment_id = #{assignmentId}
     and question_id = #{questionId}
     and score = #{score}
     and sequence = #{sequence}
     and is_deleted = #{isDeleted}

order by sequence asc

mapper.delete:
update assignment_question set is_deleted = 1, delete_time = now(), update_time = now()
where id in
 #{id}

mapper.select:
SELECT aq.assignment_id AS assignment_id, -- 真实字段：assignment_question.assignment_id aq.question_id AS question_id, -- 真实字段：assignment_question.question_id aq.sequence AS question_sequence, -- 真实字段：assignment_question.sequence -- 聚合知识点ID（来自 assignment_kp.kp_id，真实字段） GROUP_CONCAT(DISTINCT ak.kp_id ORDER BY ak.kp_id SEPARATOR ',') AS kp_ids, -- 聚合知识点名称（来自 knowledge_point.title，真实字段） GROUP_CONCAT(DISTINCT kp.title ORDER BY ak.kp_id SEPARATOR ',') AS kp_titles FROM assignment_question aq -- 关联 assignment 表：获取 course_id（作业属于课程，真实关联） LEFT JOIN assignment a ON aq.assignment_id = a.id AND a.is_deleted = 0 -- 作业未删除（真实字段） -- 关联 assignment_kp 表：双字段精准匹配（真实字段+真实关联） LEFT JOIN assignment_kp ak ON aq.assignment_id = ak.assignment_id -- 作业ID匹配 AND aq.sequence = ak.sequence -- 题目序号匹配 AND ak.is_required = 1 -- 真实字段：有效关联 -- 关联 knowledge_point 表：补充知识点名称（真实表+真实字段） LEFT JOIN knowledge_point kp ON ak.kp_id = kp.id AND kp.is_deleted = 0 -- 真实字段：知识点未删除 -- 筛选条件：仅用真实存在的字段 WHERE a.course_id = #{courseId} -- 从 assignment 表取 course_id（核心修正） AND aq.assignment_id IN
#{assId}
AND aq.is_deleted = 0 -- 真实字段：题目未删除 -- 分组字段：均为 assignment_question 真实字段，避免分组错误 GROUP BY aq.assignment_id, aq.question_id, aq.sequence -- 排序字段：真实字段，保持顺序 ORDER BY aq.assignment_id ASC, aq.sequence ASC
