mapper.select:


     and id = #{id}
     and assignment_id = #{assignmentId}
     and question_id = #{questionId}
     and score = #{score}
     and sequence = #{sequence}
     and is_deleted = #{isDeleted}

order by sequence asc

mapper.delete:
update assignment_question set is_deleted = 1, delete_time = now(), update_time = now()
where id in
 #{id}

mapper.select:
SELECT aq.assignment_id AS assignment_id, -- 作业ID aq.question_id AS question_id, -- 题目ID aq.sequence AS question_sequence, -- 题目序号 q.knowledge_point AS kp_ids, -- 核心：直接取题目自身的知识点（无冗余） -- 可选：拼接知识点名称（需要关联 knowledge_point 表，精准匹配题目知识点） GROUP_CONCAT(DISTINCT kp.title SEPARATOR ',') AS kp_titles FROM assignment_question aq -- 关联 question 表：获取题目自身的知识点（核心关联） JOIN question q ON aq.question_id = q.id AND q.is_deleted = 0 AND q.knowledge_point IS NOT NULL -- 过滤无知识点的题目 -- 关联 assignment 表：过滤课程ID（确保课程匹配） JOIN assignment a ON aq.assignment_id = a.id AND a.is_deleted = 0 -- 可选：关联 knowledge_point 表，获取知识点名称（不影响筛选，仅优化显示） LEFT JOIN knowledge_point kp ON FIND_IN_SET(kp.id, q.knowledge_point) -- 精准匹配题目知识点ID AND kp.is_deleted = 0 WHERE a.course_id = #{courseId} -- 课程ID筛选 AND aq.assignment_id IN
#{assId}
AND aq.is_deleted = 0 -- 过滤已删除题目 GROUP BY aq.assignment_id, aq.question_id, aq.sequence, q.knowledge_point -- 按题目+知识点分组 ORDER BY aq.assignment_id ASC, aq.sequence ASC;
